sys_variable_value.value:
(function execute(inputs, outputs) {
  try {
    // Check membership using sys_user_grmember table
    function isUserInGroup(userId, groupId) {
      var membership = new GlideRecord("sys_user_grmember");
      membership.addQuery("user", userId);
      membership
        .addQuery("group", groupId)
        .addOrCondition("group.name", groupId);
      membership.query();
      return membership.hasNext();
    }

    var operator = inputs.operator;
    var groups = inputs.groups || [];
    var user = inputs.user || gs.getUserID();

    var isMember = false;
    var groupId;

    if (operator === "OR") {
      for (var i = 0; i < groups.length; i++) {
        groupId = groups[i].trim();
        if (isUserInGroup(user, groupId)) {
          isMember = true;
          break;
        }
      }
    } else if (operator === "AND") {
      isMember = true;
      for (var i = 0; i < groups.length; i++) {
        groupId = groups[i].trim();
        if (!isUserInGroup(user, groupId)) {
          isMember = false;
          break;
        }
      }
    }

    outputs.is_member = isMember;
    outputs.is_success = true;
  } catch (error) {
    gs.error("Error in group membership check: " + error.message);
    outputs.is_member = false;
    outputs.is_success = false;
  }
})(inputs, outputs);

